import React from "react";


// reactstrap components
import {
  Card,
  CardHeader,
  CardBody,
  CardTitle,
  Collapse,
  NavItem,
  NavLink,
  Nav,
  TabContent,
  TabPane,
  Row,
  Col
} from "reactstrap";



class Nuis extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      horizontalTabs: "profile",
      verticalTabs: "profile",
      verticalTabsIcons: "home",
      pageTabs: "home",
      openedCollapses: ["collapseOne"]
    };
  }
  // with this function we create an array with the opened collapses
  // it is like a toggle function for all collapses from this page
  collapsesToggle = (e, collapse) => {
    e.preventDefault();
    let openedCollapses = this.state.openedCollapses;
    if (openedCollapses.includes(collapse)) {
      this.setState({
        openedCollapses: openedCollapses.filter(item => item !== collapse)
      });
    } else {
      openedCollapses.push(collapse);
      this.setState({
        openedCollapses: openedCollapses
      });
    }
  };
  // with this function we change the active tab for all the tabs in this page
  changeActiveTab = (e, tabState, tadName) => {
    e.preventDefault();
    this.setState({
      [tabState]: tadName
    });
  };
  render() {
    return (
      <>
        <div className="content">
        <h1>ss</h1>
        <Col md="6">
              <Card>
                <CardHeader>
                  <h5 className="card-category">Navigation Pills</h5>
                  <CardTitle tag="h3">Vertical Tabs With Icons</CardTitle>
                </CardHeader>
                <CardBody>
                  <Row>
                    <Col lg="3" md="6">
                      {/* color-classes: "nav-pills-primary", "nav-pills-info", "nav-pills-success", "nav-pills-warning","nav-pills-danger" */}
                      <Nav
                        className="nav-pills-info nav-pills-icons flex-column"
                        pills
                      >
                        <NavItem>
                          <NavLink
                            data-toggle="tab"
                            href="#pablo"
                            className={
                              this.state.verticalTabsIcons === "home"
                                ? "active"
                                : ""
                            }
                            onClick={e =>
                              this.changeActiveTab(
                                e,
                                "verticalTabsIcons",
                                "home"
                              )
                            }
                          >
                            <i className="tim-icons icon-istanbul" />
                            Home
                          </NavLink>
                        </NavItem>
                        <NavItem>
                          <NavLink
                            data-toggle="tab"
                            href="#pablo"
                            className={
                              this.state.verticalTabsIcons === "settings"
                                ? "active"
                                : ""
                            }
                            onClick={e =>
                              this.changeActiveTab(
                                e,
                                "verticalTabsIcons",
                                "settings"
                              )
                            }
                          >
                            <i className="tim-icons icon-settings" />
                            Settings
                          </NavLink>
                        </NavItem>
                      </Nav>
                    </Col>
                    <Col md="8">
                      <TabContent activeTab={this.state.verticalTabsIcons}>
                        <TabPane tabId="home">
                          Collaboratively administrate empowered markets via
                          plug-and-play networks. Dynamically procrastinate B2C
                          users after installed base benefits. <br />
                          <br />
                          Dramatically visualize customer directed convergence
                          without revolutionary ROI.
                        </TabPane>
                        <TabPane tabId="settings">
                          Efficiently unleash cross-media information without
                          cross-media value. Quickly maximize timely
                          deliverables for real-time schemas. <br />
                          <br />
                          Dramatically maintain clicks-and-mortar solutions
                          without functional solutions.
                        </TabPane>
                      </TabContent>
                    </Col>
                  </Row>
                </CardBody>
              </Card>
            </Col>
        </div>
      </>
    );
  }
}

export default Nuis;
